import React from 'react';
import './App.css';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import markerIconPng from "leaflet/dist/images/marker-icon.png";
import { Icon } from 'leaflet'

function App() {

  const berkeley = [37.8719, -122.2585];
  const sather_gate = [37.8703, -122.2595];
  const mlk = [37.8692, -122.2597];
  const moffitt = [37.8725, 122.2608];

  function BerkeleyMarker (props) {
    return (
      <Marker position={props.pos} icon={new Icon({iconUrl: markerIconPng, iconSize: [25, 41], iconAnchor: [12, 41]})}>
          <Popup>
            {props.desc}
          </Popup>
        </Marker>
    )
  }

  return (
    <div className="leaflet-container">
      <MapContainer center={berkeley} zoom={17} scrollWheelZoom={true}>
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <BerkeleyMarker pos={sather_gate} desc='Sather Gate'/>
        <BerkeleyMarker pos={mlk} desc='MLK Jr. Building (ASUC Student Union)'/>
      </MapContainer>
    </div>
  );
}

export default App;
